select orderId from collection where collectionMemberId in

	#{id}

group by orderId

	select count(*) from orders where id not in

 		  #{nos}

 	AND companyId = #{companyId}

	select o.orderNumber,u.name,u.phone,o.borrowMoneyWay,o.borrowTimeLimit,o.shouldReturnTime,ord.overdueNumberOfDays,ord.interestPenaltySum,
	o.orderCreateTime,ord.realityBorrowMoney,ord.makeLoans,ord.realityAccount
	 from orders o,orderdetails ord,user u

		AND ord.orderId not in

 		 	 #{nos}

	    AND o.realtime < #{realtime}
		AND o.id = ord.orderId
		AND u.id = o.userId
		AND o.companyId = #{companyId}

			AND u.name = #{name}


			AND u.phone = #{phone}


			AND o.id = #{id}


	group by ord.orderId 
	limit #{page},#{pagesize}

select defe.deferBeforeReturntime,defe.interestOnArrears,deset.onceDeferredDay,defe.deferAfterReturntime from 
deferred defe,deferred_settings deset,orders o where o.companyId = deset.companyId and o.id = defe.orderid
and o.id = #{orderId}

select o.orderNumber,u.name,u.phone,o.borrowMoneyWay,o.borrowTimeLimit,o.shouldReturnTime,ord.overdueNumberOfDays,ord.interestPenaltySum,
o.orderCreateTime,ord.realityBorrowMoney,ord.makeLoans,ord.realityAccount,colme.reallyName,o.overdueGrade,ord.surplus_money,
col.collectionTime,col.collectionStatus,o.orderStatus from orders o,orderdetails ord,collection col,collection_member colme,user u

	AND o.id = ord.orderId
	AND u.id = o.userId
	AND colme.collectionMemberId = col.collectionMemberId
	AND o.id = col.orderId
	AND o.companyId = #{companyId}

		AND ord.realityBorrowMoney = #{realityBorrowMoney}


		AND ord.makeLoans = #{makeLoans}


		AND ord.shouldAlsoInterest = #{shouldAlsoInterest}


		AND ord.shouldReapyMoney = #{shouldReapyMoney}


		AND o.id = #{orderId}


		AND u.name LIKE concat('%',#{name},'%')


		AND u.phone LIKE concat('%',#{phone},'%')


		AND overdue.grade = #{grade}


		AND c.collectionStatus = #{collectionStatus}


		AND cmem.collectionMemberId = #{collectionMemberId}


		AND c.collectionTime between #{start_time} and #{end_time}


group by o.id
limit #{page},#{pagesize}

select o.borrowTimeLimit,orders.*,u.name,u.phone,c.collectionStatus,cmem.reallyName,overdue.grade, c.collectionTime,o.orderNumber,o.id,o.borrowMoneyWay,defe.deferBeforeReturntime,defe.deferAfterReturntime from collection c,collection_member cmem,orderdetails orders,overdue_class overdue,orders o,user u,deferred defe
AND u.id = o.userId AND o.id = orders.orderId AND u.companyId = overdue.overdue AND o.id = defe.orderId AND collection.collectionMemberId = cmem.collectionMemberId AND cmem.companyId = u.companyId AND o.orderNumber = #{orderNumber} AND o.companyId = #{companyId}
order by orders.id desc limit #{page},#{pagesize}

select  o.orderCreateTime  from orders o

		AND o.companyId = #{companyId} 

			AND o.borrowMoneyType = #{borrowMoneyType}


			AND o.orderCreateTime between #{start_time} and#{end_time}


	GROUP BY o.orderCreateTime

select  o.orderCreateTime,count(o.id) as collection_count from orders o

		AND o.companyId = #{companyId} 

			AND o.borrowMoneyType = #{borrowMoneyType}


			AND o.orderCreateTime between #{start_time} and #{end_time}


	GROUP BY o.orderCreateTime
	limit #{page},#{pagesize}

select colme.reallyName,c.collectionTime,count(c.collectionTime) as collection_count from 
collection c,collection_member colme

	AND c.collectionMemberId = colme.collectionMemberId 

		AND collectionTime between #{statu_time} and#{end_time}


GROUP BY c.collectionMemberId
limit #{page},#{pagesize}

SELECT collectionTime from collection


			AND collectionMemberId = #{collectionMemberId}


			AND collectionTime between #{start_time} and #{end_time}

	SELECT cmember.reallyName,count(c.collectionId) as collection_count,c.collectionTime FROM 
	collection c,collection_member cmember

 		AND c.collectionMemberId=cmember.collectionMemberId

			AND c.collectionMemberId = #{collectionMemberId}


			AND c.collectionTime between #{start_time} and #{end_time}


 	GROUP BY c.collectionTime
 	limit #{page},#{pagesize}

