select 	DATE_FORMAT(o.orderCreateTime,'%Y-%m-%d') as orderCreateTime,count(o.orderCreateTime) as collection_count,sum(ora.makeLoans) as makeLoans,
sum(interestPenaltySum) as interestPenaltySum from orderdetails ora,orders o 

	AND ora.orderId = o.id
	AND o.companyId = #{companyId} 

		AND o.orderCreateTime between #{start_time} and #{end_time}


GROUP BY DATE_FORMAT(o.orderCreateTime,'%Y-%m-%d')
limit #{page},#{pagesize}

select 	DATE_FORMAT(o.orderCreateTime,'%Y-%m-%d') as orderCreateTime,count(o.orderCreateTime) as collection_count,sum(ora.makeLoans) as makeLoans,
