mapper.insert:
insert into user (id, companyId, sourceId, 
  name, phone, pwd, transactionPwd, 
  registeTime, loginTime, loginStatus, 
  registeClient, useMarket, drainageOfPlatformId
  )
values (#{id,jdbcType=INTEGER}, #{companyid,jdbcType=INTEGER}, #{sourceid,jdbcType=INTEGER}, 
  #{name,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{pwd,jdbcType=VARCHAR}, #{transactionpwd,jdbcType=VARCHAR}, 
  #{registetime,jdbcType=VARCHAR}, #{logintime,jdbcType=VARCHAR}, #{loginstatus,jdbcType=VARCHAR}, 
  #{registeclient,jdbcType=VARCHAR}, #{usemarket,jdbcType=VARCHAR}, #{drainageofplatformid,jdbcType=INTEGER}
  )

mapper.update:
update user
set loginStatus = #{loginStatus},
loginTime = #{loginTime}
where phone = #{newPhone} and companyId = #{companyId}

update user
set loginStatus = #{loginStatus}
where id = #{userId} and company = #{company}

mapper.select:
	select u.id,u.name,u.phone,u.registeTime,uat.attestationStatus userattestationstatus,b.attestationStatus bankattestationstatus,o.attestationStatus operaattestationstatus from  
	user u LEFT JOIN user_attestation uat on uat.userId=u.id LEFT JOIN bankcard b on b.userId=u.id LEFT JOIN operator o on o.userId=u.id
	where companyId=#{companyId} and u.ifBlacklist=0
        
        	and u.name=#{name}
        
        
        	and u.phone=#{phone}
        
        
           and u.registeTime between #{registeTimeStart} and #{registeTimeEnd}
        
        
           and uat.attestationStatus=#{userattestationstatus}
        
         
           and b.attestationStatus=#{bankattestationstatus}
        
         
           and o.attestationStatus=#{operaattestationstatus}
        
     order by u.registeTime desc

